import React, { useState, useEffect } from "react";
import {  useParams } from "react-router-dom";
import axios from "axios";
import {Card, CardContent, List, ListItem} from "@material-ui/core";

const User = () => {
  const [user, setUser] = useState({
    fullName: "",
    companyName: "",
    email: "",
    phoneNo: "",
    address: ""
  });
  const { id } = useParams();
  useEffect(() => {
    loadUser();
  }, []);
  const loadUser = async () => {
    const res = await axios.get(`http://localhost:3003/users/${id}`);
    setUser(res.data);
  };
  return (
    <div container>
      <Card>
        <CardContent>
      <h1>{id}</h1>
        <List>
          <ListItem>
          Full Name: {user.fullName}
          </ListItem>
          <ListItem>Company Name: {user.companyName}</ListItem>
          <ListItem>Email: {user.email}</ListItem>
          <ListItem>Contact: {user.phoneNo}</ListItem>
          <ListItem>Address: {user.address}</ListItem>

        </List>

      </CardContent>
      </Card>
    </div>
  );
};

export default User;
